exports.init=function(config, app)
{	
	process.on('message', function(m, socket){
		eval(
			m, 
			global, 
			null, 
			function(err, m){
				if(err)
				{
					console.log(err);
					process.send(err, socket);
				}
				else
				{
					process.send(m, socket);
				}
			}, 
			function(message){
				console.log(message);
			});
	});
}