exports.init=function(config, app)
{	
	process.on('message', function(m, socket){
	    switch(m)
	    {
	        case 'rules':
                process.send({rules:$.language()});
            break;
            default:
                eval(
                    m, 
                    global, 
                    null, 
                    function(err, m){
                        if(err)
                        {
                            console.log(err);
                            process.send(err, socket);
                        }
                        else
                        {
                            process.send(m, socket);
                        }
                    }, 
                    function(message){
                        console.log(message);
                    });
                break;
	    }
	});
};