$.language({
	keywords:{'of':['de', 'd\''], 'to':['que', 'qu\'']},
	understand:function(cmd, context, next)
	{
		if(cmd.text.indexOf('rappelle')<0)
			return false;
		
		if(!cmd.time || !cmd.time.isValid())
			return next('quand ca ?');
			
		console.log(cmd.text);
		
		var action=this.findInfo(this, 'keywords', cmd.text);

		action=$.grep($.map(this.keywords[action], function(item){
			return cmd.text.indexOf(item);
		}).sort(function(a,b) { return a-b; }), function(item, index){
			return item>=0;
		});
		
		cmd.action=cmd.text.substring(action[0], cmd.text.length-1);
		
		return cmd.action;
	},
	process:function(cmd, context, callback, next)
	{
		callback(null, 'il est temps '+cmd.action+' '+cmd.time.relative());
	},
	
});