$.language({
	keywords:{'of':['de', 'd\''], 'to':['que', 'qu\'']},
	actions:{},
	understand:function(cmd, next)
	{
		console.log(cmd);
		if(!cmd.time || !cmd.time.isValid())
			return next('quand ca ?');
		if(cmd.text.indexOf('rappelle')<0)
			return false;
		
		console.log(cmd.text);
		
		var action=this.findInfo(this, 'keywords', cmd.text);
		

		cmd.action=cmd.text.substring(action, cmd.text.length-2);
		
		return cmd.action;
	},
	process:function(cmd, callback, next)
	{
		callback(null, 'il est temps '+cmd.action+' '+cmd.time.relative());
	},
	
});