module.exports=require('generic-middleware')(1, function(i, arg){
	if(i==0)
		return {text:arg};
	return arg;
}, function(error, args)
{
	args[3](error);
});
module.exports.init=function(config, app)
{
	app.use=module.exports.use;
	var next=app.next;
	app.next=function(error){
		next(error);
		next.args[3](error);
	};
}